<?global $TEMPLATE_OPTIONS;?><script type="text/javascript">$(document).ready(function() {	var bStores = <?=($TEMPLATE_OPTIONS["STORES_SOURCE"]["CURRENT_VALUE"] != 'IBLOCK' ? 'true' : 'false')?>;	$('.stores_text_wrapp a').each(function() {		var href = '';		if (bStores){			if((href = $(this).attr('data-storehref'))){				$(this).attr('href', href)			}		}		else{			if((href = $(this).attr('data-iblockhref'))){				$(this).attr('href', href)			}		}	});});</script>